interface MetorialMapper<T> {
    transformTo: (input: any) => any;
    transformFrom: (input: any) => T;
}

interface MetorialObjectMapperField {
    fromKey: string;
    mapper: MetorialMapper<any>;
}

interface MetorialUnionMapperOption {
    type: string;
    mapper: MetorialMapper<any>;
}

declare let mtMap: {
    object: <T = any>(properties: Record<string, MetorialObjectMapperField>) => MetorialMapper<T>;
    objectField: (fromKey: string, mapper: MetorialMapper<any>) => MetorialObjectMapperField;
    union: (options: MetorialUnionMapperOption[]) => MetorialMapper<any>;
    unionOption: (type: string, mapper: MetorialMapper<any>) => MetorialUnionMapperOption;
    array: <T = any>(mapper: MetorialMapper<T>) => MetorialMapper<T>;
    date: () => MetorialMapper<Date>;
    passthrough: () => MetorialMapper<any>;
};

export { type MetorialMapper, mtMap };
